import React, { useState } from 'react';

function SumCalculator() {
  const [num1, setNum1] = useState('');
  const [num2, setNum2] = useState('');
  const [result, setResult] = useState('');
  const [error, setError] = useState('');

  const handleNum1Change = (e) => {
    setNum1(e.target.value);
  };

  const handleNum2Change = (e) => {
    setNum2(e.target.value);
  };

  const handleSum = () => {
    // Verificar si los valores ingresados son números válidos
    const parsedNum1 = parseFloat(num1);
    const parsedNum2 = parseFloat(num2);

    if (isNaN(parsedNum1) || isNaN(parsedNum2)) {
      setError('Por favor, ingrese números válidos.');
      setResult('');
    } else {
      setError('');
      const sum = parsedNum1 + parsedNum2;
      setResult(`Resultado: ${sum}`);
    }
  };

  return (
    <div>
      <h1>Calculadora de Suma</h1>
      <input
        type="text"
        placeholder="Número 1"
        value={num1}
        onChange={handleNum1Change}
      />
      <input
        type="text"
        placeholder="Número 2"
        value={num2}
        onChange={handleNum2Change}
      />
      <button onClick={handleSum}>Sumar</button>
      <p>{error}</p>
      <p>{result}</p>
    </div>
  );
}

export default SumCalculator;
