import React, { useState } from 'react';

function Calculator() {
  const [num1, setNum1] = useState('');
  const [num2, setNum2] = useState('');
  const [operation, setOperation] = useState('add'); // 'add', 'subtract', 'multiply', 'divide'
  const [result, setResult] = useState('');
  const [error, setError] = useState('');

  const handleNum1Change = (e) => {
    setNum1(e.target.value);
  };

  const handleNum2Change = (e) => {
    setNum2(e.target.value);
  };

  const handleOperationChange = (e) => {
    setOperation(e.target.value);
  };

  const handleCalculate = () => {
    // Verificar si los valores ingresados son números válidos
    const parsedNum1 = parseFloat(num1);
    const parsedNum2 = parseFloat(num2);

    if (isNaN(parsedNum1) || isNaN(parsedNum2)) {
      setError('Por favor, ingrese números válidos.');
      setResult('');
    } else {
      setError('');

      let calculatedResult;
      switch (operation) {
        case 'add':
          calculatedResult = parsedNum1 + parsedNum2;
          break;
        case 'subtract':
          calculatedResult = parsedNum1 - parsedNum2;
          break;
        case 'multiply':
          calculatedResult = parsedNum1 * parsedNum2;
          break;
        case 'divide':
          if (parsedNum2 === 0) {
            setError('No se puede dividir por cero.');
            setResult('');
          } else {
            calculatedResult = parsedNum1 / parsedNum2;
            setError('');
          }
          break;
        default:
          setError('Operación no válida.');
          setResult('');
          break;
      }

      if (!isNaN(calculatedResult)) {
        setResult(`Resultado: ${calculatedResult}`);
      }
    }
  };

  return (
    <div>
      <h1>Calculadora</h1>
      <input
        type="text"
        placeholder="Número 1"
        value={num1}
        onChange={handleNum1Change}
      />
      <input
        type="text"
        placeholder="Número 2"
        value={num2}
        onChange={handleNum2Change}
      />
      <select onChange={handleOperationChange} value={operation}>
        <option value="add">Suma</option>
        <option value="subtract">Resta</option>
        <option value="multiply">Multiplicación</option>
        <option value="divide">División</option>
      </select>
      <button onClick={handleCalculate}>Calcular</button>
      <p>{error}</p>
      <p>{result}</p>
    </div>
  );
}

export default Calculator;
